import React, { Component } from "react";
import ProductList from "./ProductList";
import ProductForm from "./ProductForm";
import Test from "../../components/test";
import { BrowserRouter, Route } from "react-router-dom";
import { Link, NavLink } from "react-router-dom";

import API from "../../services/api";

class Products extends Component {
  state = {
    products: [],
    user: {
      role: "ADMIN"
    }
  };

  componentDidMount = () => {
    API.getAllProducts()
      .then(products => this.setState({ products: products }))
      .catch(err => console.log(err));
  };

  render() {
    const shoes = this.state.products.filter(
      products => products.category === "shoes"
    );
    const mobiles = this.state.products.filter(
      products => products.category === "mobile"
    );

    return (
      <div className="body">
        <div className=" btn-container">
          {this.state.user.role == "ADMIN" && (
            <a href="/productForm">
              <button
                onClick={this.navigateProductForm}
                className="btn btn-success btn-lg mr-5 my-3 "
              >
                Add Product{" "}
              </button>
            </a>
          )}
        </div>
        <Test title="ادريس " />
        <ProductForm />
        <ProductList products={shoes} />
        <ProductList products={mobiles} />
      </div>
    );
  }
}

export default Products;
