import React, { Component } from "react";
import "./ProductList.css";
import API from "../../services/api";

class EditForm extends Component {
  state = {
    editProduct: {}
  };
  componentDidMount() {
    console.log("param id: ", this.props.match.params["id"]);
    const id = this.props.match.params["id"];
    API.getAllProductById(id)
      .then(editProduct => this.setState({ editProduct }))
      .catch(err => console.log(err));
  }

  editSubmit(event) {
    event.preventDefault();
    const form = event.target;
    // henter data
    const data = new FormData(form);
    // lager en tom formData
    let formData = {};

    for (let key of data.keys()) {
      const value = form.elements[key].value;
      // fyller formData
      formData[key] = value;
    }

    formData["rate"] = 0;
    const merchant = {
      fristName: "Salem",
      lastName: "ali",
      email: "ali@olsomet.no"
    };
    const body = {
      product: formData,
      merchant
    };

    console.log("data: ", formData);
    console.log("put body: ", body);

    API.putProduct(body)
      .then(newProduct => console.log("new Product: ", newProduct))
      .catch(err => console.log(err));
  }

  updateProductName = event => {
    const name = event.target.value;
    let product = this.state.editProduct;
    product.name = name;
    this.setState({ editProduct: product });
  };

  updateProductCategory = event => {
    const category = event.target.value;
    let product = this.state.editProduct;
    product.category = category;
    this.setState({ editProduct: product });
  };

  render() {
    return (
      <div className="col-sm-6 my-3 mx-auto ml">
        <form onSubmit={this.editSubmit}>
          <div className="form-group form-container">
            <label className="mr-auto ml-0">Name</label>
            <input
              value={this.state.editProduct.name}
              onChange={this.updateProductName}
              name="name"
              type="text"
              className="form-control"
              placeholder=""
            />
          </div>
          <div className="form-group">
            <label>Release Year </label>
            <input
              value={this.state.editProduct.releaseYear}
              name="releaseYear"
              type="text"
              className="form-control"
              placeholder="Enter release year "
            />
          </div>
          <div className="form-group">
            <label>Quantity </label>
            <input
              value={this.state.editProduct.quantity}
              name="quantity"
              type="number"
              className="form-control"
              placeholder="Enter quantity "
            />
          </div>
          <div className="form-group">
            <label>Category </label>
            <input
              value={this.state.editProduct.category}
              name="category"
              type="text"
              className="form-control"
              placeholder="Enter category "
            />
          </div>
          <button className="btn btn-primary">Submit</button>
        </form>
      </div>
    );
  }
}

export default EditForm;
