import React, { Component } from "react";
import Nav from "./components/Nav";
import { BrowserRouter, Route } from "react-router-dom";
import Home from "./components/Home";
import About from "./components/About";
import API from "./services/api";
import ProductList from "./components/products/ProductList";

import "./App.css";

class App extends Component {
  state = {
    products: []
  };

  componentDidMount = () => {
    API.getAllProducts()
      .then(products => this.setState({ products: products }))
      .catch(err => console.log(err));
  };

  render() {
    const shoes = this.state.products.filter(
      products => products.category === "shoes"
    );
    const mobiles = this.state.products.filter(
      products => products.category === "mobile"
    );
    const categories = [shoes, mobiles];
    return (
      <BrowserRouter>
        <div className="App">
          <Nav />
          <ProductList products={shoes} />
          <ProductList products={mobiles} />

          <Route exact path="/" component={Home} />
          <Route path="/about" component={About} />
        </div>
      </BrowserRouter>
    );
  }
}

export default App;
