export default class API {
  // returns Promise
  // .then .catch
  static getAllProducts() {
    let url = "http://localhost:9997/products";
    return new Promise((resolve, reject) => {
      fetch(url)
        .then(res => res.json())
        .then(products => resolve(products))
        .catch(error => reject(error));
    });
  }
}
